import React, { useState, useEffect } from "react";
import styles from "./AvailableMeal.module.css";
import MealItem from "./MealItem";
import Card from "../UI/Card";
import axios from "axios";

const AvailableMeal = () => {
  const [data, setData] = useState([]);
  const [isLoading, setisLoading] = useState(false);
  const sendRequest = async () => {
    setisLoading(true);
    const response = await axios.get(
      "https://meals-55c67-default-rtdb.firebaseio.com/Meals.json"
    );

    const resdata = await response.data;
    const mealsData = [];
    for (let d in resdata) {
      const meal = {
        id: d,
        name: resdata[d].name,
        description: resdata[d].description,
        price: resdata[d].price,
      };

      setData((prev) => {
        return [...prev, meal];
      });
    }
    setisLoading(false);
  };

  useEffect(() => {
    sendRequest();
  }, []);

  console.log(data);

  if (isLoading) {
    return (
      <div>
        <p className={styles.loading}>Loading....</p>
      </div>
    );
  }

  const Data = data.map((element) => {
    return (
      <MealItem
        key={element.id}
        id={element.id}
        name={element.name}
        description={element.description}
        price={element.price}
      />
    );
  });
  return (
    <div className={styles.meals}>
      {!isLoading && (
        <Card>
          <ul>{Data}</ul>
        </Card>
      )}
    </div>
  );
};

export default AvailableMeal;
