import React, { useState, useEffect } from "react";
import styles from "./AvailableMeal.module.css";
import MealItem from "./MealItem";
import Card from "../UI/Card";
import axios from "axios";

const DUMMY_MEALS = [
  {
    id: "m1",
    name: "Sushi",
    description: "Finest fish and veggies",
    price: 22.99,
  },
  {
    id: "m2",
    name: "Schnitzel",
    description: "A german specialty!",
    price: 16.5,
  },
  {
    id: "m3",
    name: "Barbecue Burger",
    description: "American, raw, meaty",
    price: 12.99,
  },
  {
    id: "m4",
    name: "Green Bowl",
    description: "Healthy...and green...",
    price: 18.99,
  },
];

const AvailableMeal = () => {
  const mealsData = [];
  const [data, setData] = useState(mealsData);
  const sendRequest = async () => {
    const response = await axios.get(
      "https://meals-55c67-default-rtdb.firebaseio.com/Meals.json"
    );

    const resdata = await response.data;
    for (let d in resdata) {
      const meal = {
        id: d,
        name: resdata[d].name,
        description: resdata[d].description,
        price: resdata[d].price,
      };

      setData(data.push(meal));
    }
    console.log(data);
  };
  useEffect(() => {
    sendRequest();
  }, []);

  const Data = DUMMY_MEALS.map((element) => {
    return (
      <MealItem
        key={element.id}
        id={element.id}
        name={element.name}
        description={element.description}
        price={element.price}
      />
    );
  });
  return (
    <div className={styles.meals}>
      <Card>
        <ul>{Data}</ul>
      </Card>
    </div>
  );
};

export default AvailableMeal;
